using Demonstrator.NRLSAdapter.Helpers;
using System;
using Xunit;

namespace DemonstratorTest.NRLSAdapter
{
    public class JwtTests
    {
        [Fact]
        public void JwtFactory_Valid_JWT()
        {
            //Token generated using:
            //
            // scope = JwtScopes.Write
            // orgCode = ORG1
            // roleProfileId = fakeRoleId
            // asid = 20000000017
            // endpoint = https://nrls.com/fhir/documentreference
            // tokenOrigin = https://demonstrator.com
            // tokenStart = 2018-04-01T10:00:30+00:00

            var expectedToken = "eyJhbGciOiJub25lIiwidHlwIjoiSldUIn0..";
            var tokenStart = new DateTime(2018, 4, 1, 10, 00, 30, DateTimeKind.Utc);

            var actualToken = JwtFactory.Generate(JwtScopes.Write, "ORG1", "fakeRoleId", "20000000017", "https://nrls.com/fhir/documentreference", "https://demonstrator.com", "DocumentReference", tokenStart);

            Assert.Equal(expectedToken, actualToken);
        }

        [Fact]
        public void JwtFactory_Invalid_JWT()
        {
            //Token generated using:
            //
            // scope = JwtScopes.Write
            // orgCode = ORG1
            // roleProfileId = fakeRoleId
            // asid = 20000000017
            // endpoint = https://nrls.com/fhir/documentreference
            // tokenOrigin = https://demonstrator.com
            // tokenStart = 2018-04-01T10:00:30+00:00

            var expectedToken = "eyJhbGciOiJub25lIiwidHlwIjoiSldUIn0..";
            var tokenStart = new DateTime(2018, 4, 1, 20, 00, 30, DateTimeKind.Utc);

            var actualToken = JwtFactory.Generate(JwtScopes.Write, "ORG2", "fakeRoleId", "20000000017", "https://nrls.com/fhir/documentreference", "https://demonstrator.com", "DocumentReference", tokenStart);

            Assert.NotEqual(expectedToken, actualToken);
        }

    }
}
